mapper.insert:
INSERT INTO T_COMM_BOARD( /*  COM_BOARD  */
	 BLBD_CD
	, NOTI_NO
	, TITLE
	, CNTN
	, INQ_CNT
	, RCMD_CNT
	, ANSW_YN
	, HRANK_NOTI_NO
	, ANSW_ORDER
	, ANSW_LEVL
	, FILE_ID
	, DEL_YN
	, INPT_ID
	, INPT_DTTM
	, INPT_IP
	, CHGE_ID
	, CHGE_DTTM
	, CHGE_IP
)VALUES(
	 #{BLBD_CD}
	, (SELECT COALESCE(MAX(NOTI_NO),0)+1 AS CNT FROM T_COMM_BOARD A WHERE BLBD_CD = #{BLBD_CD})
	, #{TITLE}
	, #{CNTN}
	, 0
	, #{RCMD_CNT}
	, #{ANSW_YN}
	, #{HRANK_NOTI_NO}
	, #{ANSW_ORDER}
	, #{ANSW_LEVL}
	, #{FILE_ID}
	, '0'
	, #{USER_ID_SRV}
	, NOW()
	, #{USER_CON_IPADDR_SRV}
	, #{USER_ID_SRV}
	, NOW()
	, #{USER_CON_IPADDR_SRV}
)

mapper.update:
UPDATE /* updateInqCount */
	T_COMM_BOARD
SET
	INQ_CNT 	= #{INQ_CNT}+1
WHERE BLBD_CD 	= #{BLBD_CD}
	AND NOTI_NO = #{NOTI_NO}

UPDATE   /* updateBoardContents */
	T_COMM_BOARD
SET
	TITLE 			= #{TITLE}
	,FILE_ID 		= #{FILE_ID}
	,CHGE_ID        = #{USER_ID_SRV}
	,CHGE_DTTM      = NOW()
	,CHGE_IP        = #{USER_CON_IPADDR_SRV}
	,CNTN			= #{CNTN}
WHERE BLBD_CD 		= #{BLBD_CD}
	AND NOTI_NO 	= #{NOTI_NO}

UPDATE	/* deleteBoardContents */ 
	T_COMM_BOARD
SET
	DEL_YN 			= '1'
	,CHGE_ID        = #{USER_ID_SRV}
	,CHGE_DTTM      = NOW()
	,CHGE_IP        = #{USER_CON_IPADDR_SRV}
WHERE BLBD_CD   	= #{BLBD_CD}
	AND NOTI_NO 	= #{NOTI_NO}

mapper.delete:
DELETE FROM T_COMM_BOARD_FILE
WHERE FILE_ID 	 = #{FILE_ID}
	AND FILE_SEQ = #{FILE_SEQ}

mapper.select:
/* searchBoardContents */ with t1 as ( select ROW_NUMBER() over(order by NOTI_NO desc) AS RNUM, AA.* from ( SELECT BOARD.BLBD_CD , BOARD.NOTI_NO , BOARD.TITLE , BOARD.CNTN , BOARD.INQ_CNT , BOARD.RCMD_CNT , BOARD.ANSW_YN , BOARD.HRANK_NOTI_NO , BOARD.ANSW_ORDER , BOARD.ANSW_LEVL , BOARD.FILE_ID , case COALESCE(BF.FILE_CNT, 0) when 0 then '0' else '1' end AS FILE_YN , BOARD.DEL_YN , BOARD.INPT_ID , CONCAT(U.KORN_NM , '(' , U.EMP_NO , ')') AS INPT_EMP_NO , DATE_FORMAT(BOARD.INPT_DTTM,'%Y%m%d%H%i%s') INPT_DTTM, -- BOARD.INPT_DTTM , BOARD.INPT_IP , BOARD.CHGE_ID , BOARD.CHGE_DTTM , BOARD.CHGE_IP, COALESCE(BR.REPLY_CNT, 0) AS REPLY_CNT FROM T_SYTM_USER U , T_COMM_BOARD BOARD LEFT OUTER JOIN ( SELECT COALESCE(COUNT(*),0) AS REPLY_CNT ,BLBD_CD ,NOTI_NO FROM T_COMM_BOARD_REPLY GROUP BY BLBD_CD, NOTI_NO ) BR ON BOARD.BLBD_CD = BR.BLBD_CD AND BOARD.NOTI_NO = BR.NOTI_NO LEFT OUTER JOIN ( SELECT COALESCE(COUNT(*),0) AS FILE_CNT ,FILE_ID FROM T_COMM_BOARD_FILE GROUP BY FILE_ID ) BF ON BOARD.FILE_ID = BF.FILE_ID WHERE BOARD.BLBD_CD = #{srchBoardCd} AND BOARD.DEL_YN = '0' AND BOARD.INPT_ID = U.USER_ID ORDER BY NOTI_NO DESC ) AA ), CTE_MAIN as ( WITH recursive t2 AS ( SELECT CONVERT(LPAD(BOARD.RNUM, 3, '0'), char(100)) as SortOrder, BOARD.* FROM t1 BOARD WHERE ANSW_LEVL = '0' UNION ALL SELECT CONCAT(c.SortOrder, '|', BOARD.RNUM) as BOARD_ORDER, BOARD.* FROM t1 BOARD INNER JOIN t2 c ON c.NOTI_NO = BOARD.HRANK_NOTI_NO ) select ROW_NUMBER() over(order by NOTI_NO desc) AS RBNUM, t2.* from t2 WHERE 1=1
AND t2.TITLE LIKE CONCAT('%', #{srchCond} ,'%')
AND INSTR(REGEXP_REPLACE(UPPER(t2.CNTN),'<[^>]*>|\&([^;])*;', ''), UPPER(#{srchCond})) > 0
AND t2.INPT_EMP_NO LIKE CONCAT('%', #{srchCond}, '%')
AND DATE_FORMAT(t2.INPT_DTTM, '%Y%m%d') BETWEEN #{FROM_DATE} AND #{TO_DATE}
) select * from CTE_MAIN
WHERE CTE_MAIN.RBNUM BETWEEN #{START_NUM} AND #{END_NUM}
order by SortOrder

/* searchBoardContentsCnt */ with t1 as ( SELECT ROW_NUMBER() over(order by NOTI_NO desc) AS RNUM , AA.* from ( SELECT BOARD.BLBD_CD , BOARD.NOTI_NO , BOARD.TITLE , BOARD.CNTN , BOARD.INQ_CNT , BOARD.RCMD_CNT , BOARD.ANSW_YN , BOARD.HRANK_NOTI_NO , BOARD.ANSW_ORDER , BOARD.ANSW_LEVL , BOARD.FILE_ID , case COALESCE(BF.FILE_CNT, 0) when 0 then '0' else '1' end AS FILE_YN , BOARD.DEL_YN , BOARD.INPT_ID , CONCAT(U.KORN_NM , '(' , U.EMP_NO , ')') AS INPT_EMP_NO , BOARD.INPT_DTTM , BOARD.INPT_IP , BOARD.CHGE_ID , BOARD.CHGE_DTTM , BOARD.CHGE_IP, COALESCE(BR.REPLY_CNT, 0) AS REPLY_CNT FROM T_SYTM_USER U , T_COMM_BOARD BOARD LEFT OUTER JOIN ( SELECT COALESCE(COUNT(*),0) AS REPLY_CNT ,BLBD_CD ,NOTI_NO FROM T_COMM_BOARD_REPLY GROUP BY BLBD_CD, NOTI_NO ) BR ON BOARD.BLBD_CD = BR.BLBD_CD AND BOARD.NOTI_NO = BR.NOTI_NO LEFT OUTER JOIN ( SELECT COALESCE(COUNT(*),0) AS FILE_CNT ,FILE_ID FROM T_COMM_BOARD_FILE GROUP BY FILE_ID ) BF ON BOARD.FILE_ID = BF.FILE_ID WHERE BOARD.BLBD_CD = #{srchBoardCd} AND BOARD.DEL_YN = '0' AND BOARD.INPT_ID = U.USER_ID ORDER BY NOTI_NO DESC ) AA ), CTE_MAIN as ( WITH recursive t2 AS ( SELECT CONVERT(BOARD.ANSW_LEVL, char(100)) as SortOrder, BOARD.* FROM t1 BOARD WHERE ANSW_LEVL = '0' UNION ALL SELECT CONCAT(c.SortOrder, '|', BOARD.ANSW_LEVL) as BOARD_ORDER, BOARD.* FROM t1 BOARD INNER JOIN t2 c ON c.NOTI_NO = BOARD.HRANK_NOTI_NO ) select * from t2 ) select COUNT(*) AS TOTALCNT from CTE_MAIN WHERE 1=1
AND CTE_MAIN.TITLE LIKE CONCAT('%', #{srchCond} ,'%')
AND INSTR(REGEXP_REPLACE(UPPER(CTE_MAIN.CNTN),'<[^>]*>|\&([^;])*;', ''), UPPER(#{srchCond})) > 0
AND CTE_MAIN.INPT_EMP_NO LIKE CONCAT('%', #{srchCond}, '%')
AND DATE_FORMAT(CTE_MAIN.INPT_DTTM, '%Y%m%d') BETWEEN #{FROM_DATE} AND #{TO_DATE}

