mapper.select:
/* searchBoardList */ SELECT BM.BLBD_CD , BM.BLBD_NM , BM.BLBD_SECT_CD , BM.PAGE_CNT , BM.ANSW_YN , BM.REPLY_YN , BM.APND_YN , BM.MGR_CD , US.EMP_NO AS MGR_EMP_NO , US.KORN_NM AS MGR_NM , BM.USED_YN FROM T_COMM_BOARD_MASTER BM LEFT OUTER JOIN T_SYTM_USER US ON BM.MGR_CD = US.USER_ID WHERE 1=1
AND BM.BLBD_NM like CONCAT('%' , #{BOARD_NM} , '%')
AND BM.BLBD_CD = #{BOARD_CD}
AND BM.USED_YN = #{USED_YN}
ORDER BY BM.BLBD_SECT_CD, BM.BLBD_CD, BM.BLBD_NM

