mapper.select:
/* searchBoardCardContents */ SELECT BB.* FROM ( SELECT B.BLBD_CD ,B.NOTI_NO ,B.TITLE ,B.CNTN ,B.INQ_CNT ,B.FILE_ID ,B.INPT_ID ,B.INPT_IP ,B.INPT_DTTM ,B.CHGE_ID ,B.CHGE_IP ,B.CHGE_DTTM , CONCAT(U.KORN_NM , '(' , U.EMP_NO , ')') AS INPT_EMP_NO -- 작성자 이름(작성자 아이디) , ROW_NUMBER() OVER(ORDER BY B.INPT_DTTM DESC) AS RNUM FROM T_COMM_BOARD B INNER JOIN T_SYTM_USER U ON B.INPT_ID = U.USER_ID WHERE B.BLBD_CD = #{BOARD_CD} AND DATE_FORMAT(B.INPT_DTTM, '%Y%m%d') BETWEEN #{FROM_DATE} AND #{TO_DATE} AND DEL_YN = '0'
AND UPPER(B.TITLE) LIKE CONCAT('%' , UPPER(#{SEARCH_COND}) , '%')
-- AND DBMS_LOB.CHARINDEX(REGEXP_REPLACE(UPPER(B.CNTN),'<[^>]*>|\&([^;])*;'), UPPER(#{SEARCH_COND})) > 0 AND CHARINDEX(UPPER(B.CNTN),UPPER(#{SEARCH_COND})) > 0
-- ORDER BY INPT_DTTM DESC )BB
WHERE BB.RNUM BETWEEN #{START_NUM} AND #{END_NUM}
ORDER BY RNUM

 

	SELECT COUNT(*) AS TOTALCNT
	from T_WEEKREPORT
	WHERE 1=1
	
		
	    AND RPT_YEAR = #{RPT_YEAR}
	    AND RPT_MONTH = #{RPT_MONTH}
	    
	
	
	    AND DEPT_CD = #{DEPT_CD}
    
	
	    AND TITLE LIKE CONCAT('%', #{TITLE}, '%')

WITH CTE_MAIN AS ( SELECT ROW_NUMBER() over(order by W.RPT_NO desc) AS RBNUM , W.RPT_NO , W.TITLE , W.DEPT_CD , W.RPT_YEAR , W.RPT_MONTH , W.RPT_CNT , W.FILE_ID , W.INPT_DTTM , W.INPT_ID , U.KORN_NM from T_WEEKREPORT W LEFT OUTER JOIN T_SYTM_USER U ON W.INPT_ID = U.USER_ID
WHERE 1 = 1
AND W.RPT_YEAR = #{RPT_YEAR} AND W.RPT_MONTH = #{RPT_MONTH}
AND W.DEPT_CD = #{DEPT_CD}
AND W.TITLE LIKE CONCAT('%', #{TITLE}, '%')
) SELECT *, DATE_FORMAT(INPT_DTTM, '%Y%m%d%H%i%s') AS REG_DATE from CTE_MAIN WHERE CTE_MAIN.RBNUM BETWEEN #{START_NUM} AND #{END_NUM} order by RBNUM

