mapper.select:
SELECT USER_ID, KEYSEQ, SEQ, LVL_NO, LVL_COLOR, LVL_BGCOLOR, LVL_FONT, LVL_NAME, MEMO_TITLE, MEMO_CONTENTS, '' AS EDITCOL, 0 AS RTYPE , CHGE_DTTM FROM T_COMM_MEMO WHERE USER_ID = #{USER_ID_SRV}
AND (MEMO_TITLE LIKE CONCAT('%', #{SEARCHDATA} ,'%') OR MEMO_CONTENTS LIKE CONCAT('%', #{SEARCHDATA} ,'%'))
ORDER BY SEQ

 
	SELECT USER_ID, KEYSEQ, SEQ, LVL_NO, LVL_COLOR, LVL_BGCOLOR, LVL_FONT, LVL_NAME, MEMO_TITLE ,MEMO_CONTENTS, CHGE_DTTM
	      ,(SELECT MAX(SEQ) FROM T_COMM_MEMO) AS MAXSEQ
	FROM T_COMM_MEMO
	WHERE USER_ID = #{USER_ID_SRV}
	AND   KEYSEQ = #{KEYSEQ}

mapper.insert:
INSERT INTO T_COMM_MEMO
(
	USER_ID, KEYSEQ, SEQ, LVL_NO, LVL_COLOR, LVL_BGCOLOR, LVL_FONT, LVL_NAME, MEMO_TITLE, MEMO_CONTENTS, INPT_ID, INPT_IP, INPT_DTTM, CHGE_ID, CHGE_IP, CHGE_DTTM
)VALUES(
	 #{USER_ID_SRV}
	, #{KEYSEQ} 
	, #{SEQ}
	, #{LVL_NO}
	, #{LVL_COLOR}
	, #{LVL_BGCOLOR}
	, #{LVL_FONT}
	, #{LVL_NAME}
	, #{MEMO_TITLE}
	, #{MEMO_CONTENTS}
	, #{USER_ID_SRV}
	, #{USER_CON_IPADDR_SRV}
	, NOW()
	, #{USER_ID_SRV}
	, #{USER_CON_IPADDR_SRV}
	, NOW()
)

mapper.update:
	UPDATE 
		T_COMM_MEMO
	SET
		  SEQ = #{SEQ}  
		, LVL_NO = #{LVL_NO}
		, LVL_COLOR = #{LVL_COLOR}
		, LVL_BGCOLOR = #{LVL_BGCOLOR}
		, LVL_FONT = #{LVL_FONT}
		, LVL_NAME = #{LVL_NAME}
		, MEMO_TITLE 	= #{MEMO_TITLE}
		, MEMO_CONTENTS = #{MEMO_CONTENTS}
		, CHGE_ID       = #{USER_ID_SRV}
		, CHGE_DTTM     = NOW()
		, CHGE_IP       = #{USER_CON_IPADDR_SRV}		
	WHERE KEYSEQ = #{KEYSEQ}
 	 AND  USER_ID = #{USER_ID_SRV}

mapper.delete:
	DELETE FROM T_COMM_MEMO 
	WHERE KEYSEQ = #{KEYSEQ}
 	 AND  USER_ID = #{USER_ID_SRV}
