mapper.select:
SELECT M.YYYYMM , M.USER_ID , M.SEND_DTTM , M.RESULT , M.RESULT_MSG , U.KORN_NM , (SELECT DEPT_KORN_NM FROM T_SYTM_DEPT WHERE DEPT_CD = U.DEPT_CD) DEPT_NM FROM T_HRD_SALARYSENDMAILRESULT M, T_SYTM_USER U WHERE M.USER_ID = U.USER_ID
AND M.YYYYMM = #{YYYYMM}
ORDER BY U.KORN_NM

mapper.insert:
INSERT INTO T_HRD_MAILCONTETNS
(
	  SEQ
	, MAIL_SUBJECT
	, USER_ID
	, MAIL_CONTENTS
	, INPT_ID
	, INPT_IP
	, INPT_DTTM
	, CHGE_ID
	, CHGE_IP
	, CHGE_DTTM  
)VALUES(
    #{SEQ}
    , #{MAIL_SUBJECT}
    , #{USER_ID}
    , #{MAIL_CONTENTS}
    , #{USER_ID_SRV}
    , #{USER_CON_IPADDR_SRV}
    , NOW()
    , #{USER_ID_SRV}
    , #{USER_CON_IPADDR_SRV}
    , NOW()
	)
ON 
DUPLICATE KEY
	UPDATE 
	    MAIL_SUBJECT 	= #{MAIL_SUBJECT}
	    , USER_ID 		= #{USER_ID}
	    , MAIL_CONTENTS = #{MAIL_CONTENTS}
	    , CHGE_ID 		= #{USER_ID_SRV}
	    , CHGE_IP 		= #{USER_CON_IPADDR_SRV}
	    , CHGE_DTTM 	= NOW()

INSERT INTO T_HRD_SALARYSENDMAILRESULT
(
	  YYYYMM
	, USER_ID
	, SEND_DTTM
	, RESULT
	, RESULT_MSG
)VALUES(
    #{YYYYMM}
    , #{USER_ID}
    ,  NOW()
    , #{RESULT}
    , #{RESULT_MSG}
	)
ON 
DUPLICATE KEY
	UPDATE 
	    SEND_DTTM 	= NOW()
	    , RESULT 		= #{RESULT}
	    , RESULT_MSG = #{RESULT_MSG}

mapper.delete:
DELETE FROM T_HRD_WORKDAY
WHERE USER_ID = #{USER_ID}
AND WORK_DATE = #{WORK_DATE}

    AND WORK_SEQ = #{WORK_SEQ}

mapper.update:
INSERT INTO T_HRD_WORKDAY
(USER_ID, WORK_DATE, WORK_SEQ, WORK_TYPE, WORK_CONTENTS, WORK_TIME, INPT_ID, INPT_DTTM, CHGE_ID, CHGE_DTTM
)VALUES(
	#{USER_ID}, #{WORK_DATE}, #{WORK_SEQ}, #{WORK_TYPE}, #{WORK_CONTENTS}, #{WORK_TIME}, #{USER_ID_SRV}, NOW(), #{USER_ID_SRV}, NOW()
)			
ON 
DUPLICATE KEY
	UPDATE 
	    WORK_TYPE 	= #{WORK_TYPE}
	    , WORK_CONTENTS 	= #{WORK_CONTENTS}
	    , WORK_TIME 		= #{WORK_TIME}
	    , CHGE_ID 		= #{USER_ID_SRV}
	    , CHGE_DTTM = NOW()
WHERE USER_ID 	= #{USER_ID}	
AND WORK_DATE = #{WORK_DATE}
AND WORK_SEQ = #{WORK_SEQ}
