mapper.update:
call
DECLARE
	V_CNT NUMBER(3);
	V_FILECNT NUMBER(3);
BEGIN
	UPDATE T_SYTM_USER 
	SET 
	    THEME_ID 		= #{THEME_ID}
	WHERE USER_ID 		= #{USER_ID_SRV};
END

mapper.select:
SELECT *                          
FROM  T_SYTM_USER 
WHERE USER_ID = #{USER_ID}
	AND  PASSWORD   =  CONVERT(#{CUR_PASSWORD}, CHAR) 	
	AND  COMPANY_CD = #{COMPANY_CD}
