mapper.insert:
INSERT INTO T_SALES_PROJECT
(
	  PRJT_CODE
	, CLIENT_CODE
	, PRJT_NAME
	, USER_NAME
	, USER_TEL
	, USER_CELL
	, USER_EMAIL
	, SALES_USER
	, REF_URL
	, SALES_TYPE_CODE
	, SALES_STATUS
	, REMK
	, INPT_ID
	, INPT_IP
	, INPT_DTTM
	, CHGE_ID
	, CHGE_IP
	, CHGE_DTTM  
)VALUES(
    #{PRJT_CODE}
    , #{CLIENT_CODE}
    , #{PRJT_NAME}
    , #{USER_NAME}
    , #{USER_TEL}
    , #{USER_CELL}
    , #{USER_EMAIL}
    , #{SALES_USER}
    , #{REF_URL}
    , #{SALES_TYPE_CODE}
    , #{SALES_STATUS}
    , #{REMK}
    , #{USER_ID_SRV}
    , #{USER_CON_IPADDR_SRV}
    , NOW()
    , #{USER_ID_SRV}
    , #{USER_CON_IPADDR_SRV}
    , NOW()
	);

mapper.update:
UPDATE T_SALES_PROJECT 
SET 
    CLIENT_CODE 	= #{CLIENT_CODE}
    , PRJT_NAME 	= #{PRJT_NAME}
    , USER_NAME 	= #{USER_NAME}
    , USER_TEL 		= #{USER_TEL}
    , USER_CELL 	= #{USER_CELL}
    , USER_EMAIL 	= #{USER_EMAIL}
    , SALES_USER	= #{SALES_USER}
    , REF_URL 		= #{REF_URL}
    , SALES_TYPE_CODE 	= #{SALES_TYPE_CODE}
    , SALES_STATUS 	= #{SALES_STATUS}
    , REMK 			= #{REMK}
    , CHGE_ID 		= #{USER_ID_SRV}
    , CHGE_DTTM 	= NOW()
    , CHGE_IP 		= #{USER_CON_IPADDR_SRV}
WHERE PRJT_CODE 	= #{PRJT_CODE}

mapper.select:
SELECT PRJT_CODE , SCHD_CODE , SCHD_SDATE , SCHD_EDATE , SCHD_TITLE , SCHD_STATUS , REMK FROM T_SALES_SCHEDULE WHERE PRJT_CODE = #{PRJT_CODE}
AND SCHD_CODE = #{SCHD_CODE}
ORDER BY SCHD_SDATE ASC

mapper.insert:
INSERT INTO T_SALES_SCHEDULE
(
	  PRJT_CODE
	, SCHD_CODE
	, SCHD_SDATE
	, SCHD_EDATE
	, SCHD_TITLE
	, SCHD_STATUS
	, REMK
	, INPT_ID
	, INPT_IP
	, INPT_DTTM
	, CHGE_ID
	, CHGE_IP
	, CHGE_DTTM  
)VALUES(
    #{PRJT_CODE}
    , #{SCHD_CODE}
    , #{SCHD_SDATE}
    , #{SCHD_EDATE}
    , #{SCHD_TITLE}
    , #{SCHD_STATUS}
    , #{REMK}
    , #{USER_ID_SRV}
    , #{USER_CON_IPADDR_SRV}
    , NOW()
    , #{USER_ID_SRV}
    , #{USER_CON_IPADDR_SRV}
    , NOW()
	);

mapper.update:
UPDATE T_SALES_SCHEDULE 
SET 
      SCHD_SDATE 	= #{SCHD_SDATE}
    , SCHD_EDATE 	= #{SCHD_EDATE}
    , SCHD_TITLE 	= #{SCHD_TITLE}
    , SCHD_STATUS 	= #{SCHD_STATUS}
    , REMK 			= #{REMK}
    , CHGE_ID 		= #{USER_ID_SRV}
    , CHGE_DTTM 	= NOW()
    , CHGE_IP 		= #{USER_CON_IPADDR_SRV}
WHERE PRJT_CODE 	= #{PRJT_CODE}	
AND SCHD_CODE 	= #{SCHD_CODE}

mapper.delete:
DELETE FROM T_SALES_SCHEDULE 
WHERE PRJT_CODE = #{PRJT_CODE}
AND  SCHD_CODE = #{SCHD_CODE}

mapper.select:
SELECT PRJT_CODE , SCHD_CODE , FILE_ID , FILE_SEQ , SAVE_FILE_NM , OTXT_FILE_NM , FILE_SIZE , FILE_TYPE , FILE_PATH_NM FROM T_SALES_FILE WHERE PRJT_CODE = #{PRJT_CODE}
AND SCHD_CODE = #{SCHD_CODE}
ORDER BY SCHD_CODE, FILE_ID

mapper.insert:
INSERT INTO T_SALES_FILE
(
	PRJT_CODE
	, SCHD_CODE
	, FILE_ID
	, FILE_SEQ
	, SAVE_FILE_NM
	, OTXT_FILE_NM
	, FILE_SIZE
	, FILE_TYPE
	, FILE_PATH_NM
	, INPT_ID
	, INPT_DTTM
	, INPT_IP
	, CHGE_ID
	, CHGE_DTTM
	, CHGE_IP
)VALUES(
	#{PRJT_CODE}
	, #{SCHD_CODE}
	, #{FILE_ID}
	, (SELECT COALESCE(MAX(FILE_SEQ),0)+1 FROM T_SALES_FILE F WHERE FILE_ID = #{FILE_ID})
	, #{SAVE_FILE_NM}
	, #{OTXT_FILE_NM}
	, #{FILE_SIZE}
	, #{FILE_TYPE}
	, #{FILE_PATH_NM}
	, #{USER_ID_SRV}
	, NOW()
	, #{USER_CON_IPADDR_SRV}
	, #{USER_ID_SRV}
	, NOW()
	, #{USER_CON_IPADDR_SRV}
)

mapper.delete:
DELETE FROM T_SALES_FILE
WHERE FILE_ID 	 = #{FILE_ID}
	AND FILE_SEQ = #{FILE_SEQ}

mapper.insert:
