    , #{CLIENT_NAME}
    , #{ADDR_BASIC}
    , #{ADDR_DTL}
    , #{ZIP_NUMB}
    , #{TELP_NUMB}
    , #{HOME_URL}
    , #{CLIENT_PIC}
    , #{CLIENT_TYPE}
    , #{USER_ID_SRV}
    , NOW()
    , #{USER_CON_IPADDR_SRV}
    , #{USER_ID_SRV}
    , NOW()
    , #{USER_CON_IPADDR_SRV}
	);

mapper.update:
UPDATE T_SALES_CLIENT 
SET 
    CLIENT_CODE 	= #{CLIENT_CODE}
    , CLIENT_NAME 	= #{CLIENT_NAME}
    , ADDR_BASIC 	= #{ADDR_BASIC}
    , ADDR_DTL 		= #{ADDR_DTL}
    , ZIP_NUMB 		= #{ZIP_NUMB}
    , TELP_NUMB 	= #{TELP_NUMB}
    , HOME_URL		= #{HOME_URL}
    , CLIENT_PIC 	= #{CLIENT_PIC}
    , CLIENT_TYPE 	= #{CLIENT_TYPE}
    , CHGE_ID 		= #{USER_ID_SRV}
    , CHGE_DTTM 	= NOW()
    , CHGE_IP 		= #{USER_CON_IPADDR_SRV}
WHERE SEQ_NO 		= #{SEQ_NO}
