mapper.select:
/* checkNewMsgCode */
SELECT MSGE_CD
FROM T_SYTM_MSG
WHERE MSGE_CD IN

    #{item.MSGE_CD}

mapper.insert:
/* insertMsg */
	INSERT INTO T_SYTM_MSG	
	(
	    MSGE_CD
	    , MSGE_FLAG_CD 
	    , KORN_MSGE
	    , ENGL_MSGE 
	    , USED_YN
	    , INPT_ID 
	    , INPT_DTTM 
	    , INPT_IP
	    , CHGE_ID 
	    , CHGE_DTTM 
	    , CHGE_IP
	)VALUES(
	    #{MSGE_CD}
	  , #{MSGE_FLAG_CD}
	  , #{KORN_MSGE}
	  , #{ENGL_MSGE}
	  , #{USED_YN}
	  , #{USER_ID_SRV}
	  , NOW()
	  , #{USER_CON_IPADDR_SRV}
	  , #{USER_ID_SRV}
	  , NOW()
	  , #{USER_CON_IPADDR_SRV}
	) ON DUPLICATE KEY 
		UPDATE 
			 MSGE_FLAG_CD    = #{MSGE_FLAG_CD}
			, KORN_MSGE      = #{KORN_MSGE}
			, ENGL_MSGE      = #{ENGL_MSGE}
			, USED_YN        = #{USED_YN}
			, CHGE_ID        = #{USER_ID_SRV}
			, CHGE_DTTM      = NOW()
			, CHGE_IP        = #{USER_CON_IPADDR_SRV}
	;

mapper.update:
/* updateMsg */
UPDATE T_SYTM_MSG 
SET 
	 MSGE_FLAG_CD    = #{MSGE_FLAG_CD}
	, KORN_MSGE      = #{KORN_MSGE}
	, ENGL_MSGE      = #{ENGL_MSGE}
	, USED_YN        = #{USED_YN}
	, CHGE_ID        = #{USER_ID_SRV}
	, CHGE_DTTM      = NOW()
	, CHGE_IP        = #{USER_CON_IPADDR_SRV}
WHERE MSGE_CD 		 = #{MSGE_CD}

mapper.delete:
/* deleteMsg */
DELETE FROM T_SYTM_MSG 
WHERE MSGE_CD=#{MSGE_CD}
