mapper.select:
/* selectSytmLoginLog */ WITH recursive TREE_MAIN AS ( SELECT DEPT_CD FROM T_SYTM_DEPT WHERE DEPT_CD = COALESCE(#{DEPT_CD},'1000000') UNION ALL SELECT a.DEPT_CD FROM T_SYTM_DEPT a , TREE_MAIN b WHERE b.DEPT_CD = a.HIPO_DEPT_CD ) SELECT LG.SYTM_FLAG_CD , DATE_FORMAT(LG.ALOG_DTTM, '%Y-%m-%d %H:%i:%s') AS ALOG_DTTM , LG.USER_ID , US.KORN_NM AS USER_NM , US.EMP_NO , US.DEPT_CD , DP.DEPT_KORN_NM AS DEPT_NM , US.WKGD_CD , LG.LOIN_FLAG_CD , LG.BROWSER_CD , LG.BROWSER_VERSION , LG.OS_CD , LG.OS_VERSION , LG.SCREEN_HEIGHT , LG.SCREEN_WIDTH , CONCAT(LG.SCREEN_WIDTH , '*' , LG.SCREEN_HEIGHT) AS SCREEN_SIZE , LG.MOB_YN , LG.INPT_IP FROM T_SYTM_LOGIN_LOG LG INNER JOIN T_SYTM_USER US ON LG.USER_ID = US.USER_ID LEFT OUTER JOIN T_SYTM_DEPT DP ON US.DEPT_CD = DP.DEPT_CD WHERE 1=1 AND LG.SYTM_FLAG_CD = #{SYTM_FLAG_CD}
AND DATE_FORMAT(LG.ALOG_DTTM, '%Y%m%d') >= #{FROM_DT}
AND DATE_FORMAT(LG.ALOG_DTTM, '%Y%m%d') <= #{TO_DT}
AND US.DEPT_CD IN (SELECT DEPT_CD FROM TREE_MAIN )
AND US.USER_ID = #{USER_ID}
AND LG.LOIN_FLAG_CD LIKE #{LOIN_FLAG_CD}
ORDER BY LG.ALOG_DTTM DESC

/* selectPrgmOpenLog */ WITH recursive TREE_MAIN AS ( SELECT DEPT_CD FROM T_SYTM_DEPT WHERE DEPT_CD = COALESCE(#{DEPT_CD},'1000000') UNION ALL SELECT a.DEPT_CD FROM T_SYTM_DEPT a , TREE_MAIN b WHERE b.DEPT_CD = a.HIPO_DEPT_CD )
SELECT PL.SYTM_FLAG_CD , DATE_FORMAT(PL.ALOG_DTTM, '%Y-%m-%d %H:%i:%s') AS ALOG_DTTM , PL.USER_ID , US.KORN_NM AS USER_NM , US.EMP_NO , PL.PRGM_ID , PR.PRGM_NM , PL.MENU_ID , MN.MENU_NM , MN.MODULE_CD , US.DEPT_CD , DP.DEPT_KORN_NM AS DEPT_NM , US.WKGD_CD , PL.INPT_IP FROM T_SYTM_PRGM_LOG PL INNER JOIN T_SYTM_USER US ON PL.USER_ID = US.USER_ID INNER JOIN T_SYTM_MENU MN ON PL.MENU_ID = MN.MENU_ID INNER JOIN T_SYTM_PRGM PR ON PL.PRGM_ID = PR.PRGM_ID LEFT OUTER JOIN T_SYTM_DEPT DP ON DP.DEPT_CD = US.DEPT_CD WHERE 1=1
AND PL.SYTM_FLAG_CD = #{SYTM_FLAG_CD}
AND DATE_FORMAT(PL.ALOG_DTTM, '%Y%m%d') >= #{FROM_DT}
AND DATE_FORMAT(PL.ALOG_DTTM, '%Y%m%d') <= #{TO_DT}
AND US.DEPT_CD IN (SELECT DEPT_CD FROM TREE_MAIN )
AND US.USER_ID LIKE #{USER_ID}
ORDER BY PL.ALOG_DTTM DESC

mapper.insert:
INSERT INTO T_SYTM_PRGM_LOG
(
   SYTM_FLAG_CD
   , ALOG_DTTM
   , USER_ID
   , PRGM_ID 
   , MENU_ID 
   , INPT_ID
   , INPT_DTTM 
   , INPT_IP
   , CHGE_ID 
   , CHGE_DTTM 
   , CHGE_IP
)VALUES(
   #{SYTM_FLAG_CD}
   , NOW() 
   , #{USER_ID} 
   , #{PRGM_ID} 
   , #{MENU_ID} 
   , #{USER_ID_SRV}
   , NOW()
   , #{USER_CON_IPADDR_SRV}
   , #{USER_ID_SRV}
   , NOW()
   , #{USER_CON_IPADDR_SRV}
)

INSERT INTO T_SYTM_LOGIN_LOG
