	, OS_VERSION
	, SCREEN_HEIGHT
	, SCREEN_WIDTH
	, MOB_YN
	, INPT_ID
	, INPT_DTTM
	, INPT_IP
	, CHGE_ID 
	, CHGE_DTTM
	, CHGE_IP
)VALUES(
	#{COMPANY_CD}
    , #{SYTM_FLAG_CD}
    , NOW()
    , #{USER_ID}
    , #{LOIN_FLAG_CD}
    , #{BROWSER_CD}
	, #{BROWSER_VERSION}
	, #{OS_CD}
	, #{OS_VERSION}
	, #{SCREEN_HEIGHT}
	, #{SCREEN_WIDTH}
	, #{MOB_YN}
	, #{USER_ID_SRV}
    , NOW()
    , #{USER_CON_IPADDR_SRV}
    , #{USER_ID_SRV}
    , NOW()
    , #{USER_CON_IPADDR_SRV}
)
